"use client"

import { useState, useEffect } from "react"
import { motion } from "framer-motion"
import { useNavigate, useLocation } from "react-router-dom"
import { useAuth } from "../context/AuthContext"
import "./AuthPages.css"

const LoginPage = () => {
  const navigate = useNavigate()
  const location = useLocation()
  const { login, isAuthenticated } = useAuth()
  const [formData, setFormData] = useState({
    userId: "",
    password: "",
  })
  const [errors, setErrors] = useState({})
  const [isLoading, setIsLoading] = useState(false)
  const [successMessage, setSuccessMessage] = useState("")

  useEffect(() => {
    if (isAuthenticated) {
      const from = location.state?.from?.pathname || "/dashboard"
      navigate(from, { replace: true })
    }
  }, [isAuthenticated, navigate, location])

  useEffect(() => {
    // Check for success message from registration
    if (location.state?.message) {
      setSuccessMessage(location.state.message)
      // Clear the message from location state
      window.history.replaceState({}, document.title)
    }
  }, [location])

  const handleChange = (e) => {
    const { name, value } = e.target
    setFormData((prev) => ({
      ...prev,
      [name]: value,
    }))
    // Clear error when user starts typing
    if (errors[name]) {
      setErrors((prev) => ({
        ...prev,
        [name]: "",
      }))
    }
  }

  const validateForm = () => {
    const newErrors = {}

    if (!formData.userId.trim()) {
      newErrors.userId = "User ID is required"
    }

    if (!formData.password) {
      newErrors.password = "Password is required"
    }

    setErrors(newErrors)
    return Object.keys(newErrors).length === 0
  }

  const handleSubmit = async (e) => {
    e.preventDefault()

    if (!validateForm()) {
      return
    }

    setIsLoading(true)
    setSuccessMessage("") // Clear any success messages

    const result = await login(formData.userId, formData.password)

    if (result.success) {
      // Login successful, redirect will happen automatically via useEffect
      const from = location.state?.from?.pathname || "/dashboard"
      navigate(from, { replace: true })
    } else {
      setErrors({
        submit: result.message,
      })
    }

    setIsLoading(false)
  }

  return (
    <div className="auth-page">
      <div className="auth-background" />

      <nav className="auth-nav">
        <div className="nav-container">
          <div className="nav-logo" onClick={() => navigate("/")}>
            <span className="logo-icon">🚌</span>
            <span className="logo-text">Bus-Karo</span>
          </div>
          <div className="nav-links">
            <button onClick={() => navigate("/register")} className="nav-link">
              Sign Up
            </button>
          </div>
        </div>
      </nav>

      <div className="auth-container">
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6 }}
          className="auth-card"
        >
          <div className="auth-header">
            <h1>Welcome Back</h1>
            <p>Sign in to your Bus-Karo account</p>
          </div>

          {successMessage && <div className="success-message">{successMessage}</div>}

          <form onSubmit={handleSubmit} className="auth-form">
            <div className="form-group">
              <label htmlFor="userId">User ID</label>
              <input
                type="text"
                id="userId"
                name="userId"
                value={formData.userId}
                onChange={handleChange}
                className={errors.userId ? "error" : ""}
                placeholder="Enter your user ID"
              />
              {errors.userId && <span className="error-message">{errors.userId}</span>}
            </div>

            <div className="form-group">
              <label htmlFor="password">Password</label>
              <input
                type="password"
                id="password"
                name="password"
                value={formData.password}
                onChange={handleChange}
                className={errors.password ? "error" : ""}
                placeholder="Enter your password"
              />
              {errors.password && <span className="error-message">{errors.password}</span>}
            </div>

            {errors.submit && <div className="error-message submit-error">{errors.submit}</div>}

            <motion.button
              type="submit"
              disabled={isLoading}
              whileHover={{ scale: 1.02 }}
              whileTap={{ scale: 0.98 }}
              className="auth-button primary"
            >
              {isLoading ? "Signing In..." : "Sign In"}
            </motion.button>
          </form>

          <div className="auth-footer">
            <p>
              Don't have an account?{" "}
              <button onClick={() => navigate("/register")} className="link-button">
                Create one here
              </button>
            </p>
          </div>
        </motion.div>
      </div>
    </div>
  )
}

export default LoginPage
